contracts/AddressBook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title AddressBook
 * @notice Registry of monitored contracts and addresses
 * @dev Sidecars read this to prioritize scrutiny on high-risk addresses
 * 
 * Features:
 * - Store addresses with risk categories
 * - Label DEX pools, bridges, high-value contracts
 * - Off-chain nodes apply higher scrutiny to watched addresses
 */
contract AddressBook is AccessControl {
    bytes32 public constant CURATOR_ROLE = keccak256("CURATOR_ROLE");
    
    // Risk levels
    enum RiskLevel { UNKNOWN, LOW, MEDIUM, HIGH, CRITICAL }
    
    // Address entry
    struct WatchedAddress {
        RiskLevel riskLevel;
        bytes32 labelHash;      // keccak256 of label string
        bytes32 category;       // e.g., "DEX", "BRIDGE", "LENDING"
        bool isContract;
        uint256 addedAt;
        bool active;
    }
    
    // Events
    event AddressWatched(
        address indexed addr,
        RiskLevel riskLevel,
        bytes32 indexed category,
        bytes32 labelHash,
        uint256 timestamp
    );
    
    event AddressUpdated(
        address indexed addr,
        RiskLevel riskLevel,
        bool active,
        uint256 timestamp
    );
    
    event AddressRemoved(address indexed addr, uint256 timestamp);
    
    // Storage
    mapping(address => WatchedAddress) public watchedAddresses;
    address[] public addressList;
    
    // Category statistics
    mapping(bytes32 => uint256) public categoryCount;
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(CURATOR_ROLE, admin);
    }
    
    /**
     * @notice Add an address to watchlist
     * @param addr Address to watch
     * @param riskLevel Risk category (0-4)
     * @param category Category hash (e.g., keccak256("DEX"))
     * @param label Human-readable label
     * @param isContract True if this is a contract address
     */
    function addAddress(
        address addr,
        RiskLevel riskLevel,
        bytes32 category,
        string calldata label,
        bool isContract
    ) external onlyRole(CURATOR_ROLE) {
        require(watchedAddresses[addr].addedAt == 0, "Already watched");
        
        bytes32 labelHash = keccak256(bytes(label));
        
        watchedAddresses[addr] = WatchedAddress({
            riskLevel: riskLevel,
            labelHash: labelHash,
            category: category,
            isContract: isContract,
            addedAt: block.timestamp,
            active: true
        });
        
        addressList.push(addr);
        categoryCount[category]++;
        
        emit AddressWatched(addr, riskLevel, category, labelHash, block.timestamp);
    }
    
    /**
     * @notice Update address risk level
     */
    function updateRiskLevel(address addr, RiskLevel newLevel) 
        external onlyRole(CURATOR_ROLE) 
    {
        require(watchedAddresses[addr].addedAt > 0, "Not found");
        
        watchedAddresses[addr].riskLevel = newLevel;
        
        emit AddressUpdated(addr, newLevel, watchedAddresses[addr].active, block.timestamp);
    }
    
    /**
     * @notice Deactivate an address (soft remove)
     */
    function deactivate(address addr) external onlyRole(CURATOR_ROLE) {
        require(watchedAddresses[addr].addedAt > 0, "Not found");
        
        watchedAddresses[addr].active = false;
        
        emit AddressRemoved(addr, block.timestamp);
    }
    
    /**
     * @notice Batch add addresses (gas efficient)
     */
    function addAddressBatch(
        address[] calldata addrs,
        RiskLevel[] calldata levels,
        bytes32 category
    ) external onlyRole(CURATOR_ROLE) {
        require(addrs.length == levels.length, "Array mismatch");
        require(addrs.length <= 100, "Max 100 per batch");
        
        for (uint256 i = 0; i < addrs.length; i++) {
            if (watchedAddresses[addrs[i]].addedAt == 0) {
                watchedAddresses[addrs[i]] = WatchedAddress({
                    riskLevel: levels[i],
                    labelHash: bytes32(0),
                    category: category,
                    isContract: true,
                    addedAt: block.timestamp,
                    active: true
                });
                
                addressList.push(addrs[i]);
                
                emit AddressWatched(addrs[i], levels[i], category, bytes32(0), block.timestamp);
            }
        }
        
        categoryCount[category] += addrs.length;
    }
    
    /**
     * @notice Check if address is watched
     */
    function isWatched(address addr) external view returns (bool) {
        return watchedAddresses[addr].active;
    }
    
    /**
     * @notice Get address risk level
     */
    function getRiskLevel(address addr) external view returns (RiskLevel) {
        return watchedAddresses[addr].riskLevel;
    }
    
    /**
     * @notice Get address details
     */
    function getAddress(address addr) external view returns (
        RiskLevel riskLevel,
        bytes32 labelHash,
        bytes32 category,
        bool isContract,
        bool active
    ) {
        WatchedAddress memory w = watchedAddresses[addr];
        return (w.riskLevel, w.labelHash, w.category, w.isContract, w.active);
    }
    
    /**
     * @notice Get total watched addresses
     */
    function getWatchedCount() external view returns (uint256) {
        return addressList.length;
    }
    
    /**
     * @notice Get addresses by category count
     */
    function getCategoryCount(bytes32 category) external view returns (uint256) {
        return categoryCount[category];
    }
}

contracts/ContractRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title ContractRegistry
 * @notice Central registry for all NodesCrypt contract addresses
 * @dev Single source of truth - off-chain services read this to discover contracts
 * 
 * Key features:
 * - Stores addresses by name hash (e.g., keccak256("SecurityAudit"))
 * - Tracks version number for each contract
 * - Emits events for off-chain indexing
 * - RBAC: only ADMIN can update addresses
 */
contract ContractRegistry is AccessControl {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    
    // Events for off-chain indexing
    event ContractUpdated(
        bytes32 indexed nameHash,
        string name,
        address indexed addr,
        uint256 version,
        uint256 timestamp
    );
    event ContractRemoved(bytes32 indexed nameHash, string name, uint256 timestamp);
    
    // Minimal storage: name hash → address
    mapping(bytes32 => address) public contracts;
    mapping(bytes32 => uint256) public versions;
    mapping(bytes32 => string) public names;  // For human-readable lookup
    
    // List of registered contract names for enumeration
    bytes32[] public registeredContracts;
    
    constructor(address initialAdmin) {
        _grantRole(DEFAULT_ADMIN_ROLE, initialAdmin);
        _grantRole(ADMIN_ROLE, initialAdmin);
    }
    
    /**
     * @notice Register or update a contract address
     * @param name Human-readable contract name (e.g., "SecurityAudit")
     * @param addr Contract address
     */
    function setContract(string calldata name, address addr) external onlyRole(ADMIN_ROLE) {
        require(addr != address(0), "Invalid address");
        
        bytes32 nameHash = keccak256(bytes(name));
        
        // Track if this is a new registration
        bool isNew = contracts[nameHash] == address(0);
        
        contracts[nameHash] = addr;
        versions[nameHash] += 1;
        
        if (isNew) {
            names[nameHash] = name;
            registeredContracts.push(nameHash);
        }
        
        emit ContractUpdated(nameHash, name, addr, versions[nameHash], block.timestamp);
    }
    
    /**
     * @notice Get contract address by name
     * @param name Human-readable contract name
     */
    function getContract(string calldata name) external view returns (address) {
        return contracts[keccak256(bytes(name))];
    }
    
    /**
     * @notice Get contract address by name hash (more gas efficient)
     * @param nameHash keccak256 of contract name
     */
    function getContractByHash(bytes32 nameHash) external view returns (address) {
        return contracts[nameHash];
    }
    
    /**
     * @notice Get version of a contract
     */
    function getVersion(string calldata name) external view returns (uint256) {
        return versions[keccak256(bytes(name))];
    }
    
    /**
     * @notice Get count of registered contracts
     */
    function getContractCount() external view returns (uint256) {
        return registeredContracts.length;
    }
    
    /**
     * @notice Remove a contract from registry (soft delete - just zero address)
     */
    function removeContract(string calldata name) external onlyRole(ADMIN_ROLE) {
        bytes32 nameHash = keccak256(bytes(name));
        require(contracts[nameHash] != address(0), "Contract not found");
        
        contracts[nameHash] = address(0);
        emit ContractRemoved(nameHash, name, block.timestamp);
    }
}

contracts/Governance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title Governance
 * @notice Simple governance contract for NodesCrypt
 * @dev Multisig-style approvals for critical changes
 * 
 * Features:
 * - Proposal creation and voting
 * - Timelock for execution
 * - Multi-signer approval
 * - Support for rule changes, upgrades, and parameter updates
 * 
 * Designed to integrate with Gnosis Safe for production
 */
contract Governance is AccessControl {
    bytes32 public constant PROPOSER_ROLE = keccak256("PROPOSER_ROLE");
    bytes32 public constant VOTER_ROLE = keccak256("VOTER_ROLE");
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");
    
    // Proposal types
    enum ProposalType { 
        RULE_CHANGE, 
        PARAMETER_UPDATE, 
        CONTRACT_UPGRADE, 
        ROLE_GRANT, 
        EMERGENCY 
    }
    
    enum ProposalStatus { 
        PENDING, 
        APPROVED, 
        REJECTED, 
        EXECUTED, 
        CANCELLED 
    }
    
    struct Proposal {
        ProposalType proposalType;
        ProposalStatus status;
        bytes32 contentHash;        // Hash of proposal content
        address proposer;
        uint256 createdAt;
        uint256 expiresAt;
        uint256 approvals;
        uint256 rejections;
        string description;
    }
    
    // Events
    event ProposalCreated(
        uint256 indexed proposalId,
        ProposalType proposalType,
        bytes32 contentHash,
        address indexed proposer,
        uint256 expiresAt
    );
    
    event ProposalVoted(
        uint256 indexed proposalId,
        address indexed voter,
        bool approved
    );
    
    event ProposalExecuted(uint256 indexed proposalId, uint256 timestamp);
    event ProposalCancelled(uint256 indexed proposalId, uint256 timestamp);
    
    event RuleApproved(bytes32 indexed ruleHash, uint256 proposalId, uint256 timestamp);
    event EmergencyActionTaken(bytes32 indexed actionHash, address executor, uint256 timestamp);
    
    // Storage
    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(bytes32 => bool) public approvedRules;  // ruleHash → approved
    
    uint256 public proposalCount;
    uint256 public requiredApprovals;
    uint256 public proposalDuration;  // seconds
    
    constructor(
        address admin,
        uint256 _requiredApprovals,
        uint256 _proposalDuration
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PROPOSER_ROLE, admin);
        _grantRole(VOTER_ROLE, admin);
        _grantRole(EXECUTOR_ROLE, admin);
        
        requiredApprovals = _requiredApprovals > 0 ? _requiredApprovals : 1;
        proposalDuration = _proposalDuration > 0 ? _proposalDuration : 7 days;
    }
    
    /**
     * @notice Create a new proposal
     * @param proposalType Type of proposal
     * @param contentHash Hash of proposal content (stored off-chain)
     * @param description Human-readable description
     */
    function createProposal(
        ProposalType proposalType,
        bytes32 contentHash,
        string calldata description
    ) external onlyRole(PROPOSER_ROLE) returns (uint256) {
        uint256 proposalId = proposalCount++;
        
        proposals[proposalId] = Proposal({
            proposalType: proposalType,
            status: ProposalStatus.PENDING,
            contentHash: contentHash,
            proposer: msg.sender,
            createdAt: block.timestamp,
            expiresAt: block.timestamp + proposalDuration,
            approvals: 0,
            rejections: 0,
            description: description
        });
        
        emit ProposalCreated(
            proposalId, 
            proposalType, 
            contentHash, 
            msg.sender, 
            block.timestamp + proposalDuration
        );
        
        return proposalId;
    }
    
    /**
     * @notice Vote on a proposal
     * @param proposalId Proposal to vote on
     * @param approve True to approve, false to reject
     */
    function vote(uint256 proposalId, bool approve) external onlyRole(VOTER_ROLE) {
        Proposal storage proposal = proposals[proposalId];
        
        require(proposal.createdAt > 0, "Proposal not found");
        require(proposal.status == ProposalStatus.PENDING, "Not pending");
        require(block.timestamp < proposal.expiresAt, "Expired");
        require(!hasVoted[proposalId][msg.sender], "Already voted");
        
        hasVoted[proposalId][msg.sender] = true;
        
        if (approve) {
            proposal.approvals++;
        } else {
            proposal.rejections++;
        }
        
        emit ProposalVoted(proposalId, msg.sender, approve);
        
        // Auto-approve if threshold reached
        if (proposal.approvals >= requiredApprovals) {
            proposal.status = ProposalStatus.APPROVED;
        }
    }
    
    /**
     * @notice Execute an approved proposal
     */
    function executeProposal(uint256 proposalId) external onlyRole(EXECUTOR_ROLE) {
        Proposal storage proposal = proposals[proposalId];
        
        require(proposal.status == ProposalStatus.APPROVED, "Not approved");
        
        proposal.status = ProposalStatus.EXECUTED;
        
        // Mark rule as approved if it's a rule change
        if (proposal.proposalType == ProposalType.RULE_CHANGE) {
            approvedRules[proposal.contentHash] = true;
            emit RuleApproved(proposal.contentHash, proposalId, block.timestamp);
        }
        
        emit ProposalExecuted(proposalId, block.timestamp);
    }
    
    /**
     * @notice Cancel a proposal
     */
    function cancelProposal(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];
        
        require(proposal.createdAt > 0, "Not found");
        require(
            proposal.proposer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "Not authorized"
        );
        require(proposal.status == ProposalStatus.PENDING, "Not pending");
        
        proposal.status = ProposalStatus.CANCELLED;
        emit ProposalCancelled(proposalId, block.timestamp);
    }
    
    /**
     * @notice Emergency action (bypasses normal governance for critical issues)
     */
    function emergencyAction(bytes32 actionHash) external onlyRole(DEFAULT_ADMIN_ROLE) {
        emit EmergencyActionTaken(actionHash, msg.sender, block.timestamp);
    }
    
    /**
     * @notice Check if a rule is approved
     */
    function isRuleApproved(bytes32 ruleHash) external view returns (bool) {
        return approvedRules[ruleHash];
    }
    
    /**
     * @notice Update governance parameters
     */
    function updateParameters(
        uint256 _requiredApprovals,
        uint256 _proposalDuration
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        requiredApprovals = _requiredApprovals;
        proposalDuration = _proposalDuration;
    }
    
    /**
     * @notice Get proposal details
     */
    function getProposal(uint256 proposalId) external view returns (
        ProposalType proposalType,
        ProposalStatus status,
        bytes32 contentHash,
        address proposer,
        uint256 approvals,
        uint256 rejections,
        uint256 expiresAt
    ) {
        Proposal memory p = proposals[proposalId];
        return (
            p.proposalType,
            p.status,
            p.contentHash,
            p.proposer,
            p.approvals,
            p.rejections,
            p.expiresAt
        );
    }
}

contracts/MerkleBatchAnchor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title MerkleBatchAnchor
 * @notice Gas-optimized batch anchoring for incident hashes
 * @dev Submit Merkle root instead of individual incidents
 * 
 * Flow:
 * 1. Collect N incident hashes off-chain
 * 2. Build Merkle tree, compute root
 * 3. Call anchorBatch(root, metadata) - single tx
 * 4. Store proofs off-chain for verification
 * 
 * Gas savings: ~21,000 gas per anchor vs ~50,000+ per individual log
 */
contract MerkleBatchAnchor is AccessControl {
    bytes32 public constant PUBLISHER_ROLE = keccak256("PUBLISHER_ROLE");
    
    // Events for off-chain indexing
    event BatchAnchored(
        bytes32 indexed root,
        bytes32 indexed modelId,
        uint256 batchSize,
        uint256 timestamp,
        address indexed reporter
    );
    
    event BatchVerified(bytes32 indexed root, bytes32 leaf, address verifier);
    
    // Minimal storage
    struct BatchInfo {
        uint256 timestamp;
        uint256 size;
        bytes32 modelId;
    }
    
    mapping(bytes32 => BatchInfo) public batches;  // root → info
    
    // Statistics
    uint256 public totalBatches;
    uint256 public totalIncidentsAnchored;
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PUBLISHER_ROLE, admin);
    }
    
    /**
     * @notice Anchor a batch of incidents via Merkle root
     * @param root Merkle root of incident hashes
     * @param modelId Model version identifier
     * @param batchSize Number of incidents in batch
     */
    function anchorBatch(
        bytes32 root,
        bytes32 modelId,
        uint256 batchSize
    ) external onlyRole(PUBLISHER_ROLE) {
        require(batches[root].timestamp == 0, "Already anchored");
        require(batchSize > 0 && batchSize <= 10000, "Invalid batch size");
        
        batches[root] = BatchInfo({
            timestamp: block.timestamp,
            size: batchSize,
            modelId: modelId
        });
        
        totalBatches++;
        totalIncidentsAnchored += batchSize;
        
        emit BatchAnchored(root, modelId, batchSize, block.timestamp, msg.sender);
    }
    
    /**
     * @notice Verify a leaf exists in an anchored batch
     * @param root Previously anchored Merkle root
     * @param leaf The incident hash to verify
     * @param proof Merkle proof (array of sibling hashes)
     */
    function verifyIncident(
        bytes32 root,
        bytes32 leaf,
        bytes32[] calldata proof
    ) external view returns (bool) {
        require(batches[root].timestamp > 0, "Batch not found");
        
        bytes32 computedHash = leaf;
        
        for (uint256 i = 0; i < proof.length; i++) {
            bytes32 proofElement = proof[i];
            
            if (computedHash <= proofElement) {
                computedHash = keccak256(abi.encodePacked(computedHash, proofElement));
            } else {
                computedHash = keccak256(abi.encodePacked(proofElement, computedHash));
            }
        }
        
        return computedHash == root;
    }
    
    /**
     * @notice Log verification event for audit trail
     */
    function recordVerification(bytes32 root, bytes32 leaf) external {
        require(batches[root].timestamp > 0, "Batch not found");
        emit BatchVerified(root, leaf, msg.sender);
    }
    
    /**
     * @notice Check if batch exists
     */
    function batchExists(bytes32 root) external view returns (bool) {
        return batches[root].timestamp > 0;
    }
    
    /**
     * @notice Get batch info
     */
    function getBatchInfo(bytes32 root) external view returns (
        uint256 timestamp,
        uint256 size,
        bytes32 modelId
    ) {
        BatchInfo memory info = batches[root];
        return (info.timestamp, info.size, info.modelId);
    }
    
    /**
     * @notice Get statistics
     */
    function getStats() external view returns (
        uint256 batchCount,
        uint256 totalIncidents
    ) {
        return (totalBatches, totalIncidentsAnchored);
    }
}

