// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title PolicyRegistry
 * @notice Registry for NodesCrypt security policies
 * @dev Stores policy metadata on-chain, actual policies encrypted off-chain (INCO)
 * 
 * Key features:
 * - Store policy hashes (not full policies)
 * - Track policy status (active/paused/deprecated)
 * - Per-validator policy assignments
 * - Event-first design for indexing
 */
contract PolicyRegistry is AccessControl {
    bytes32 public constant POLICY_ADMIN_ROLE = keccak256("POLICY_ADMIN_ROLE");
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    
    // Policy status enum
    enum PolicyStatus { INACTIVE, ACTIVE, PAUSED, DEPRECATED }
    
    // Policy metadata (minimal on-chain storage)
    struct Policy {
        bytes32 contentHash;      // Hash of encrypted policy content
        bytes32 encryptionKeyId;  // INCO key identifier
        PolicyStatus status;
        uint256 createdAt;
        uint256 updatedAt;
        string version;
    }
    
    // Events
    event PolicyCreated(
        bytes32 indexed policyId,
        bytes32 contentHash,
        string version,
        uint256 timestamp
    );
    
    event PolicyUpdated(
        bytes32 indexed policyId,
        bytes32 newContentHash,
        PolicyStatus status,
        uint256 timestamp
    );
    
    event PolicyAssigned(
        address indexed validator,
        bytes32 indexed policyId,
        uint256 timestamp
    );
    
    event GlobalPolicySet(bytes32 indexed policyId, uint256 timestamp);
    
    // Storage
    mapping(bytes32 => Policy) public policies;
    mapping(address => bytes32) public validatorPolicies;  // validator → policyId
    bytes32 public globalDefaultPolicy;
    
    bytes32[] public policyIds;  // For enumeration
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(POLICY_ADMIN_ROLE, admin);
    }
    
    /**
     * @notice Create a new policy
     * @param policyId Unique policy identifier
     * @param contentHash Hash of encrypted policy content (stored in INCO)
     * @param encryptionKeyId INCO encryption key identifier
     * @param version Human-readable version string
     */
    function createPolicy(
        bytes32 policyId,
        bytes32 contentHash,
        bytes32 encryptionKeyId,
        string calldata version
    ) external onlyRole(POLICY_ADMIN_ROLE) {
        require(policies[policyId].createdAt == 0, "Policy exists");
        
        policies[policyId] = Policy({
            contentHash: contentHash,
            encryptionKeyId: encryptionKeyId,
            status: PolicyStatus.ACTIVE,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            version: version
        });
        
        policyIds.push(policyId);
        
        emit PolicyCreated(policyId, contentHash, version, block.timestamp);
    }
    
    /**
     * @notice Update policy content hash
     */
    function updatePolicy(
        bytes32 policyId,
        bytes32 newContentHash,
        string calldata newVersion
    ) external onlyRole(POLICY_ADMIN_ROLE) {
        require(policies[policyId].createdAt > 0, "Policy not found");
        
        policies[policyId].contentHash = newContentHash;
        policies[policyId].version = newVersion;
        policies[policyId].updatedAt = block.timestamp;
        
        emit PolicyUpdated(policyId, newContentHash, policies[policyId].status, block.timestamp);
    }
    
    /**
     * @notice Set policy status
     */
    function setPolicyStatus(bytes32 policyId, PolicyStatus status) 
        external onlyRole(POLICY_ADMIN_ROLE) 
    {
        require(policies[policyId].createdAt > 0, "Policy not found");
        
        policies[policyId].status = status;
        policies[policyId].updatedAt = block.timestamp;
        
        emit PolicyUpdated(policyId, policies[policyId].contentHash, status, block.timestamp);
    }
    
    /**
     * @notice Assign policy to a validator
     */
    function assignPolicy(address validator, bytes32 policyId) 
        external onlyRole(POLICY_ADMIN_ROLE) 
    {
        require(policies[policyId].status == PolicyStatus.ACTIVE, "Policy not active");
        
        validatorPolicies[validator] = policyId;
        emit PolicyAssigned(validator, policyId, block.timestamp);
    }
    
    /**
     * @notice Set global default policy
     */
    function setGlobalDefaultPolicy(bytes32 policyId) 
        external onlyRole(POLICY_ADMIN_ROLE) 
    {
        require(policies[policyId].status == PolicyStatus.ACTIVE, "Policy not active");
        
        globalDefaultPolicy = policyId;
        emit GlobalPolicySet(policyId, block.timestamp);
    }
    
    /**
     * @notice Get effective policy for a validator
     * @dev Returns validator-specific policy if set, otherwise global default
     */
    function getEffectivePolicy(address validator) external view returns (bytes32) {
        bytes32 assigned = validatorPolicies[validator];
        if (assigned != bytes32(0) && policies[assigned].status == PolicyStatus.ACTIVE) {
            return assigned;
        }
        return globalDefaultPolicy;
    }
    
    /**
     * @notice Get policy content hash for off-chain retrieval
     */
    function getPolicyHash(bytes32 policyId) external view returns (bytes32) {
        return policies[policyId].contentHash;
    }
    
    /**
     * @notice Get full policy metadata
     */
    function getPolicy(bytes32 policyId) external view returns (
        bytes32 contentHash,
        bytes32 encryptionKeyId,
        PolicyStatus status,
        uint256 createdAt,
        uint256 updatedAt,
        string memory version
    ) {
        Policy memory p = policies[policyId];
        return (p.contentHash, p.encryptionKeyId, p.status, p.createdAt, p.updatedAt, p.version);
    }
    
    /**
     * @notice Get total policy count
     */
    function getPolicyCount() external view returns (uint256) {
        return policyIds.length;
    }
}

contracts/SecurityAudit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title SecurityAudit
 * @notice Immutable incident anchor for NodesCrypt security decisions
 * @dev Minimal on-chain footprint - stores only hashes and timestamps
 * 
 * Key features:
 * - Stores incident hashes (not raw data)
 * - Emits rich events for off-chain indexing
 * - RBAC: PUBLISHER role for sidecars, AUDITOR for queries
 * - Never stores PII, IPs, or raw ML features
 * 
 * What gets logged:
 * - incidentId: SHA256 hash of decision payload
 * - action: 0=PASS, 1=DELAY, 2=DROP, 3=ESCALATE
 * - modelId: identifier of ML model version
 * - riskScore: 0-100 scale
 */
contract SecurityAudit is AccessControl {
    bytes32 public constant PUBLISHER_ROLE = keccak256("PUBLISHER_ROLE");
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");
    
    // Events for off-chain indexing (rich data, cheap storage)
    event IncidentLogged(
        bytes32 indexed incidentId,
        uint8 action,
        bytes32 indexed modelId,
        uint8 riskScore,
        uint256 timestamp,
        address indexed reporter
    );
    
    event ModelRegistered(bytes32 indexed modelId, string version, uint256 timestamp);
    
    // Minimal storage: only what's needed for verification
    mapping(bytes32 => uint256) public loggedAt;  // incidentId → timestamp
    
    // Statistics (public, no sensitive data)
    uint256 public totalIncidents;
    uint256 public lastIncidentTime;
    
    // Registered models (for audit trail)
    mapping(bytes32 => bool) public registeredModels;
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PUBLISHER_ROLE, admin);
        _grantRole(AUDITOR_ROLE, admin);
    }
    
    /**
     * @notice Log a security incident
     * @param incidentId Deterministic hash of incident payload
     * @param action RL action taken (0-3)
     * @param modelId Hash of model identifier
     * @param riskScore Calculated risk (0-100)
     */
    function logIncident(
        bytes32 incidentId,
        uint8 action,
        bytes32 modelId,
        uint8 riskScore
    ) external onlyRole(PUBLISHER_ROLE) {
        require(loggedAt[incidentId] == 0, "Already logged");
        require(action <= 3, "Invalid action");
        require(riskScore <= 100, "Invalid risk score");
        
        loggedAt[incidentId] = block.timestamp;
        totalIncidents++;
        lastIncidentTime = block.timestamp;
        
        emit IncidentLogged(
            incidentId,
            action,
            modelId,
            riskScore,
            block.timestamp,
            msg.sender
        );
    }
    
    /**
     * @notice Batch log multiple incidents (gas efficient)
     * @dev Use MerkleBatchAnchor for even more efficiency
     */
    function logIncidentBatch(
        bytes32[] calldata incidentIds,
        uint8[] calldata actions,
        bytes32 modelId,
        uint8[] calldata riskScores
    ) external onlyRole(PUBLISHER_ROLE) {
        require(incidentIds.length == actions.length, "Array mismatch");
        require(incidentIds.length == riskScores.length, "Array mismatch");
        require(incidentIds.length <= 50, "Max 50 per batch");
        
        for (uint256 i = 0; i < incidentIds.length; i++) {
            if (loggedAt[incidentIds[i]] == 0) {
                loggedAt[incidentIds[i]] = block.timestamp;
                totalIncidents++;
                
                emit IncidentLogged(
                    incidentIds[i],
                    actions[i],
                    modelId,
                    riskScores[i],
                    block.timestamp,
                    msg.sender
                );
            }
        }
        lastIncidentTime = block.timestamp;
    }
    
    /**
     * @notice Register a model version for audit trail
     */
    function registerModel(bytes32 modelId, string calldata version) 
        external onlyRole(PUBLISHER_ROLE) 
    {
        registeredModels[modelId] = true;
        emit ModelRegistered(modelId, version, block.timestamp);
    }
    
    /**
     * @notice Check if incident exists
     */
    function incidentExists(bytes32 incidentId) external view returns (bool) {
        return loggedAt[incidentId] > 0;
    }
    
    /**
     * @notice Get incident timestamp
     */
    function getIncidentTime(bytes32 incidentId) external view returns (uint256) {
        return loggedAt[incidentId];
    }
    
    /**
     * @notice Get audit statistics
     */
    function getStats() external view returns (
        uint256 total,
        uint256 lastTime
    ) {
        return (totalIncidents, lastIncidentTime);
    }
}

